frappe.ui.form.on("Batch", "validate", function(frm) {
    //var d = locals[cdt][cdn];
    //msgprint(frm.docstatus);
	doc = frm.doc;

    if(!frm.doc.__isLocal){		
	days_to_expiry = frappe.datetime.get_day_diff(doc.expiry_date, frappe.datetime.nowdate())
	console.log('days diff === ' + days_to_expiry);
		doc.days_to_expiry = days_to_expiry

		if (parseInt(days_to_expiry) <= parseInt(doc.expiry_start_day) && parseInt(days_to_expiry) > 30) doc.expiry_status = "Expired Soon"
		else if (parseInt(days_to_expiry) <= 30 && parseInt(days_to_expiry) > 0)
			doc.expiry_status = "Expired Very Soon"
		else if (parseInt(days_to_expiry) <= 0)
			doc.expiry_status = "Expired"
		else	doc.expiry_status = "Open"
    }	
  //msgprint(item_code);
});
